con:config.script:
import com.eviware.soapui.support.UISupport;
import java.util.Properties;
import java.awt.Dimension;

def testcase = testRunner.testCase;

String IBANBICAboutValue = testcase.getPropertyValue("IBANBICAboutValue");
String IBANBICVersionValue = testcase.getPropertyValue("IBANBICVersionValue");

String messageText = "<html><body><div>About: ${IBANBICAboutValue}<br><br>Version: ${IBANBICVersionValue}</div></body></html>";
String messageDescription = "IBANBIC Webservice information";
String messageTitle = "About";

UISupport.showExtendedInfo(messageTitle,messageDescription,messageText,new Dimension(800, 250));

String ibanQuestion = "IBAN (No space/dasches, all in one string)";
String ibanTitle = "Belgian IBAN Converter";
String ibanDefault = "";

String ibanNumber = UISupport.prompt(ibanQuestion,ibanTitle,ibanDefault);

// Handle the cancel-click, prompt returns null when pressed cancel button
if (!ibanNumber?.trim()) {
  String[] propToRemove = new String[testcase.getPropertyCount()];
  String emptyPropValue = "";
  propToRemove = testcase.getPropertyNames();
  log.info(" Clearing "+propToRemove.size()+" property values");
  for ( int i = 0 ; i < propToRemove.size(); i++ ){
    testcase.setPropertyValue(propToRemove[i],emptyPropValue);
  }
  String cancelMessage = "No IBAN provided";
  testRunner.cancel(cancelMessage);
  UISupport.showInfoMessage(cancelMessage,"Result");
} else {
  testcase.setPropertyValue("IBANBICIbanNumber",ibanNumber);
}

return;

import com.eviware.soapui.support.UISupport;
import groovy.util.slurpersupport.GPathResult;
import org.xml.sax.*;
import com.eviware.soapui.support.types.*;
import com.eviware.soapui.model.testsuite.*;

int currentStepInd = context.currentStepIndex
TestCase testcase = testRunner.testCase;
TestStep verifyIBANStep = testcase.getTestStepAt(currentStepInd - 1)

String resultMessage = '';

try{
  String verifyIBANResponseHdrCt = verifyIBANStep.testRequest.getResponse().getContentType();

  if (verifyIBANResponseHdrCt.contains("xml")) {
  	String verifyIBANResponse = verifyIBANStep.testRequest.getResponse().getContentAsXml();

     GPathResult XMLPResponseEnv = new groovy.util.XmlSlurper().parseText(verifyIBANResponse);

  	XMLPResponseEnv.declareNamespace(soap: 'http://schemas.xmlsoap.org/wsdl/soap/')
  	               .declareNamespace(soapenv: 'http://www.w3.org/2003/05/soap-envelope')
  	               .declareNamespace(tmpuri: 'http://tempuri.org/');

     String validationResult = XMLPResponseEnv.'soapenv:Body'.'tmpuri:controleIBANResponse'.'tmpuri:controleIBANResult'.text();
     String ibanNumber = testcase.getPropertyValue("IBANBICIbanNumber");

     if ("true".equals(validationResult)) {
     	resultMessage = "IBAN ${ibanNumber} is VALID";
     } else {
     	resultMessage = "IBAN ${ibanNumber} is INVALID";
     }
  } else if (verifyIBANResponseHdrCt.startsWith("EMPTY")) {
  	resultMessage = "No response received!!";
  } else {
  	resultMessage = "Response is not an xml, unable to process.";
  }
} catch(SAXException SAXex){
	resultMessage = "Unable to process response message: "+SAXex.getMessage();
} catch(NullPointerException NPex) {
	resultMessage = "No response found: "+NPex.getMessage();
} catch(Exception ex) {
    resultMessage = "Unable to process response message: "+ex.getMessage();
} finally {
	UISupport.showInfoMessage(resultMessage);
}

return;

con:testCase.con:setupScript:
def scriptlogArea = com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log");
def soapUIlogArea = com.eviware.soapui.SoapUI.logMonitor.getLogArea("SoapUI log");
def httplogArea = com.eviware.soapui.SoapUI.logMonitor.getLogArea("http log");
def testcase = testRunner.testCase;

scriptlogArea.clear();
soapUIlogArea.clear();
httplogArea.clear();

log.info(" Running "+ testcase.testSuite.project.name+" / "+testcase.name);
